import React from "react";
import PropTypes from "prop-types";

const Login = () => {

  const loginOptions = [
    {
      name: "GitHub",
      class: "gitHub",
    },
    {
      name: "Facebook",
      class: "facebook"
    },
  ];

  return (
    <nav className="login">
      <h2>Inventory Login</h2>
      <p>Sign in to manage your store's inventory</p>
      {
        Object.keys(loginOptions).map((loginOption, index) => (
          <button
            className={loginOption[index].class}
            key={index}
            onClick={() => this.props.authenticate(loginOption.name)}
          >
            {`Login with ${loginOption.name}`}
          </button>
        ))
      }
    </nav>
  );
};

Login.propTypes = {
  authenticate: PropTypes.func,
};

export default Login;
